INSERT INTO `articles` (`article_id`, `title`, `summary`, `content`, `article_photo`, `DATETIME`, `category`, `views`, `user_id`) VALUES
(6, 'মঙ্গলবার আসছে নতুন আইফোন, আরও যেসব পণ্যের ঘোষণা দিতে পারে অ্যাপল', '১২ সেপ্টেম্বর ক্যালিফোর্নিয়ায় উন্মোচন অনুষ্ঠান', 'প্রতিবছর সেপ্টেম্বরে নতুন সব পণ্যের ঘোষণা দিয়ে থাকে অ্যাপল। এবারও ১২ সেপ্টেম্বর যুক্তরাষ্ট্রের ক্যালিফোর্নিয়ার কুপারটিনোর অ্যাপল পার্কে নতুন পণ্য ও প্রযুক্তি উন্মোচন অনুষ্ঠানের আয়োজন করবে মার্কিন প্রযুক্তিপ্রতিষ্ঠানটি। বিভিন্ন গণমাধ্যমে পাঠানো দাওয়াতপত্রে এই অনুষ্ঠানের একটি বাহারি নামও দিয়েছে অ্যাপল। ‘ওয়ান্ডারলাস্ট’। অনুষ্ঠানে আসতে যাওয়া নতুন প্রযুক্তি ও পণ্য নিয়ে বিশ্বজুড়ে চলছে নানা জল্পনা-কল্পনা। এ বছর সম্ভাব্য নতুন পণ্যের তালিকা দেখে নেওয়া যাক।প্রতিবছর সেপ্টেম্বরে নতুন সব পণ্যের ঘোষণা দিয়ে থাকে অ্যাপল। এবারও ১২ সেপ্টেম্বর যুক্তরাষ্ট্রের ক্যালিফোর্নিয়ার কুপারটিনোর অ্যাপল পার্কে নতুন পণ্য ও প্রযুক্তি উন্মোচন অনুষ্ঠানের আয়োজন করবে মার্কিন প্রযুক্তিপ্রতিষ্ঠানটি। বিভিন্ন গণমাধ্যমে পাঠানো দাওয়াতপত্রে এই অনুষ্ঠানের একটি বাহারি নামও দিয়েছে অ্যাপল। ‘ওয়ান্ডারলাস্ট’। অনুষ্ঠানে আসতে যাওয়া নতুন প্রযুক্তি ও পণ্য নিয়ে বিশ্বজুড়ে চলছে নানা জল্পনা-কল্পনা। এ বছর সম্ভাব্য নতুন পণ্যের তালিকা দেখে নেওয়া যাক।', 'uploads/article/apple.webp', '2023-09-09 00:32:04', 'smartphone', 1, 4),
(7, 'চীনে নিষেধাজ্ঞার কবলে আইফোন', 'সরকারি কর্মীদের আইফোন ব্যবহার নিষিদ্ধ করেছে চীন', 'সরকারি কর্মীদের আইফোনসহ বিদেশি প্রতিষ্ঠানের তৈরি সব ধরনের ফোন ব্যবহার নিষিদ্ধ করেছে চীন। এ নিষেধাজ্ঞার ফলে দেশটিতে সরকারি চাকরিজীবীরা অফিসের কাজে আইফোন বা অন্য কোনো বিদেশি প্রতিষ্ঠানের তৈরি ফোন ব্যবহার করতে পারবেন না। চীনের এ সিদ্ধান্তের ফলে বড় ধরনের আর্থিক ক্ষতির সম্ভাবনা থাকলেও আনুষ্ঠানিকভাবে কোনো মন্তব্য করেনি অ্যাপল। ওয়াল স্ট্রিট জার্নালের এক প্রতিবেদনে এ তথ্য জানানো হয়েছে।\r\n\r\nচীন দীর্ঘদিন ধরেই বিদেশি পণ্য ও প্রযুক্তির পরিবর্তে দেশি প্রযুক্তি ও পণ্য ব্যবহারের জন্য একটি প্রচারণা চালাচ্ছে। নতুন এ সিদ্ধান্ত সেই কার্যক্রমকে আরও গতিশীল করবে। তবে এ বিষয়ে ভিন্নমতও রয়েছে। ধারণা করা হচ্ছে, যুক্তরাষ্ট্র ও ইউরোপের দেশগুলোয় চীনা প্রযুক্তি বা প্রতিষ্ঠানের বিরুদ্ধে নিষেধাজ্ঞার পাল্টা জবাব দিতেই আইফোনসহ বিদেশি প্রতিষ্ঠানের তৈরি সব ধরনের ফোন ব্যবহার নিষিদ্ধ করেছে চীন।', 'uploads/article/apple2.webp', '2023-09-09 00:42:30', 'smartphone', 8, 4),
(8, 'পাসওয়ার্ডের বদলে পাসকি ব্যবহার করা যাবে লিংকডইন ও এক্সে', 'লিংকডইন ও এক্সে পাসওয়ার্ড ছাড়াই প্রবেশ করা যাবে', 'প্রচলিত পাসওয়ার্ডের বদলে ‘পাসকি’ সুবিধা ব্যবহার করে প্রবেশ করা যাবে পেশাজীবীদের সামাজিক যোগাযোগমাধ্যম লিংকডইন ও খুদে ব্লগ লেখার ওয়েবসাইট এক্সে (টুইটার)। নতুন এ সুবিধা দিতে এরই মধ্যে কাজ শুরু করেছে প্রতিষ্ঠান দুটি। লিংকডইন ও এক্সের কোড পর্যালোচনা করে এক ব্লগ বার্তায় এ তথ্য জানিয়েছেন অ্যাপ বিশ্লেষক স্টিভ মোসের।\r\n\r\nপাসকি পদ্ধতিতে ব্যবহারকারীর শনাক্তকরণ যেমন আঙুলের ছাপ বা স্ক্রিন লক পিন মূলত ক্লাউডের মাধ্যমে সব যন্ত্রে সংযুক্ত থাকে। ক্রিপ্টোগ্রাফিক পাসকি ক্লাউডের মাধ্যমে সব যন্ত্রে খাপ খাওয়ানো (সিনক্রোনাইজ করা) যায়। ফলে আঙুলের ছাপ বা স্ক্রিন লক পিন ব্যবহার করে সহজে যন্ত্র আনলক এবং ওয়েবসাইট ও অ্যাপে সাইনইন করা সম্ভব।পাসকি পদ্ধতি তুলনামূলক নিরাপদ। কেননা এর সাহায্যে ব্যবহারকারীর আঙুলের ছাপ বা অন্যান্য শনাক্তকরণ প্রক্রিয়ার প্রয়োজন হয়। এর ফলে দূর থেকে হ্যাকাররা অ্যাকাউন্টের দখল নিতে পারে না। তাই এরই মধ্যে গুগল, অ্যাপলসহ বিভিন্ন প্রযুক্তি প্রতিষ্ঠান এ সুবিধা চালু করেছে। তবে এ পদ্ধতিতে পাসওয়ার্ড ছাড়া ওয়েবসাইট বা অ্যাপ ব্যবহারের জন্য আগে থেকে নির্দিষ্ট যন্ত্রে সাইনইন করতে হয়।', 'uploads/article/x.webp', '2023-09-09 00:44:38', 'software', 1, 4),
(9, 'লিংকডইনেও আসছে কৃত্রিম বুদ্ধিমত্তা', 'ব্যবহারকারীদের তথ্য সাজিয়ে দেবে এই প্রযুক্তি', 'পেশাজীবীদের জনপ্রিয় সামাজিক যোগাযোগমাধ্যম লিংকডইনে যুক্ত হচ্ছে কৃত্রিম বুদ্ধিমত্তা (এআই) প্রযুক্তি। এ সুবিধা চালু হলে কৃত্রিম বুদ্ধিমত্তা প্রযুক্তি কাজে লাগিয়ে সহজেই নিজেদের প্রোফাইলে স্বয়ংক্রিয়ভাবে তথ্য যুক্ত করতে পারবেন ব্যবহারকারীরা। শুধু তা-ই নয়, চাকরিদাতা প্রতিষ্ঠানগুলোও কৃত্রিম বুদ্ধিমত্তা প্রযুক্তি কাজে লাগিয়ে নিয়োগ বিজ্ঞপ্তিতে কাজের ধরন বা বিবরণ লিখতে পারবেন। এরই মধ্যে কৃত্রিম বুদ্ধিমত্তা প্রযুক্তির টুলটি চালুর জন্য কাজও শুরু করেছে লিংকডইন।লিংকডইনের তথ্যমতে, ব্যবহারকারীরা নিজেদের দক্ষতা ও অভিজ্ঞতার তথ্য লিখে দিলেই তা সুন্দর করে সাজিয়ে লিখে দেবে কৃত্রিম বুদ্ধিমত্তা প্রযুক্তির টুলটি। ফলে সহজেই নিয়োগদাতা প্রতিষ্ঠানগুলোর দৃষ্টি আকর্ষণ করা সম্ভব হবে। অপর দিকে নিয়োগদাতা প্রতিষ্ঠানগুলো পদের নাম ও কাজের বিবরণ লিখে দিলে সে অনুযায়ী নিয়োগ বিজ্ঞপ্তি তৈরি করে দেবে টুলটি। ফলে কৃত্রিম বুদ্ধিমত্তা প্রযুক্তির টুলটি চালু হলে চাকরিপ্রার্থী ও নিয়োগদাতা প্রতিষ্ঠান উভয়ই উপকৃত হবে।', 'uploads/article/linkedin.webp', '2023-09-09 00:47:03', 'software', 4, 4),
(11, 'ফেসবুক থেকে ভিডিও ডাউনলোড করবেন যেভাবে', 'ফেসবুক থেকে ভিডিও ডাউনলোড করা যায়', 'বন্ধু বা পরিবারের সদস্যদের সঙ্গে নিয়মিত যোগাযোগের পাশাপাশি ফেসবুকে বিভিন্ন বিষয়ের ভিডিও আপলোড করেন অনেকেই। এসব ভিডিও চাইলেই ডাউনলোড করে পরে দেখা যায়। ফেসবুক থেকে ভিডিও ডাউনলোড করে কম্পিউটারে সংরক্ষণের পদ্ধতি দেখে নেওয়া যাক—\r\n\r\nভিডিও ডাউনলোডের জন্য কম্পিউটার থেকে ফেসবুকে প্রবেশ করে পছন্দের ভিডিও চালু করতে হবে। এরপর ভিডিও লিংক কপি করে ব্রাউজারে নতুন ট্যাব বা অন্য ব্রাউজার চালুর পর লিংকটি পেস্ট করতে হবে। এবার এন্টার বাটনে ক্লিক না করে লিংকে থাকা www এর পরিবর্তে mbasic লিখতে হবে। এরপর এন্টার বাটনে ক্লিক করলে নতুন একটি ট্যাবে ভিডিওটি চালু হবে। ভিডিওতে ক্লিক করলেই নতুন আর একটি ট্যাবে শুধু ভিডিওটি দেখা যাবে। সেখানে ভিডিওর ডান দিকে থাকা তিনটি ডট মেনুতে ক্লিক করে ডাউনলোড অপশন নির্বাচন করলেই ভিডিওটি কম্পিউটারের নির্দিষ্ট ফোল্ডারে খুঁজে পাওয়া যাবে।', 'uploads/article/fb_1.webp', '2023-09-09 00:49:13', 'tutorial', 0, 4),
(12, 'ইনস্টাগ্রামে ভ্যানিশ মোড চালু বা বন্ধ করবেন যেভাবে', 'ভ্যানিশ মোড ব্যবহার করে ইনস্টাগ্রামে নিরাপদে বার্ত', 'বি ও ভিডিওর পাশাপাশি ইনস্টাগ্রামে গুরুত্বপূর্ণ বার্তাও আদান-প্রদান করেন অনেকে। এসব বার্তা প্রাপক ছাড়া অন্য কেউ যেন দেখতে না পারেন, সে জন্য ভ্যানিশ মোড নামের একটি সুবিধা রয়েছে সামাজিক যোগাযোগমাধ্যমটিতে। ভ্যানিশ মোডে বার্তা পাঠালে প্রাপক বার্তা দেখার পরপরই সেটি স্বয়ংক্রিয়ভাবে মুছে যায়। ফলে সংরক্ষণ করা না থাকায় পরে অন্য কেউ বার্তাটি দেখতে পারেন না।ভ্যানিশ মোড চালুর জন্য ইনস্টাগ্রাম অ্যাপে প্রবেশ করে যে ব্যক্তিকে বার্তা পাঠাতে হবে, তাঁর চ্যাট ইনবক্সে প্রবেশ করতে হবে। এরপর চ্যাট বক্সের যেকোনো স্থানে আঙুল রেখে চেপে ধরে ওপরের দিকে সোয়াইপ করলেই ‘সোয়াইপ আপ টু টার্ন অন ভ্যানিশ মোড’ লেখা দেখা যাবে। এবার সাধারণ নিয়মে বার্তা পাঠালেই সেটি প্রাপকের কাছে চলে যাবে।', 'uploads/article/ins.webp', '2023-09-09 00:51:31', 'tutorial', 0, 4),
(13, 'খালি চোখেই ত্রিমাত্রিক ছবি ও ভিডিও দেখা যায় এই মনিটরে', 'সনি ইএলএফ-এসআর২ স্পেশাল রিয়েলিটি ডিসপ্লে মনিটর', 'থ্রি-ডি গ্লাস বা চশমা ছাড়াই ত্রিমাত্রিক ছবি বা ভিডিও দেখাতে সক্ষম নতুন মডেলের মনিটর তৈরি করেছে সনি। ‘সনি ইএলএফ-এসআর২ স্পেশাল রিয়েলিটি ডিসপ্লে’ মডেলের মনিটরটির পর্দার আকার ২৭ ইঞ্চি। ফলে বড় পর্দায় স্বচ্ছন্দে ত্রিমাত্রিক ছবি বা ভিডিও দেখা যায়। ফোরকে রেজল্যুশনের এই মনিটর চোখের নড়াচড়াও শনাক্ত করতে পারে। আর তাই মনিটরের যেকোনো পাশ থেকেই ছবি বা ভিডিও ভালোভাবে দেখা সম্ভব। \r\n\r\nমনিটরটি ব্যবহার করে সহজেই শিল্প খাতে ব্যবহার উপযোগী বিভিন্ন নকশা, অপারেশনের পরিকল্পনা করা সম্ভব। ভবনের নকশা ভালোভাবে দেখার পাশাপাশি সফটওয়্যার ও গেম তৈরিতেও ব্যবহার করা যায় মনিটরটি। ভারতের বাজারে উন্মুক্ত হওয়া মনিটরটির দাম সাত লাখ রুপি বা ৯ লাখ ২২ হাজার টাকা।', 'uploads/article/sony.webp', '2023-09-09 01:01:33', 'pc', 5, 4),
(14, 'প্রসেসর সরবরাহে ঘাটতি থাকলেও কম্পিউটার যন্ত্রাংশের দাম অপরিবর্তিত', 'প্রসেসর সরবরাহে ঘাটতি রয়েছে বাজারে', 'চলতি সপ্তাহে ঢাকার বিভিন্ন কম্পিউটার বাজার ঘুরে দেখা গেছে, চাহিদার তুলনায় প্রসেসর সরবরাহে কিছুটা ঘাটতি থাকলেও সব ধরনের কম্পিউটার যন্ত্রাংশের দাম অপরিবর্তিত রয়েছে। ঢাকার একাধিক কম্পিউটার বাজার ঘুরে উল্লেখযোগ্য যন্ত্রাংশগুলোর দাম সংগ্রহ করেছেন তাসনিম মাহফুজ।\r\n\r\nপ্রসেসর\r\nইন্টেল: কোর আই ৯ ৩.৪০-৫.৫০ গিগাহার্টজ (গি. হা.) ১২ প্রজন্ম ৭৩ হাজার ৫০০ টাকা, কোর আই ৫ (২.৬০-৪.৪০ গি. হা.) ১১ প্রজন্ম ১৪ হাজার ৮০০ টাকা এবং ১৮ হাজার টাকা, কোর আই ৩ (৩.৩০-৪.৩০ গি. হা.) ১২ প্রজন্ম ১৩ হাজার ৩০০ টাকা।\r\n\r\nএএমডি: রাইজেন ৯ (৪.৫০-৫. ৭০ গি. হা.) ৬৯ হাজার ৮০০ টাকা, রাইজেন ৭ (৩.৪০-৪.৫০ গি. হা.) ৪১ হাজার ৫০০ টাকা এবং রাইজেন ৫ (৩.৯০-৪. ৪০) ১৪ হাজার ২০০ টাকা।\r\n\r\nমাদারবোর্ড\r\nআসুস: টাফ গেমিং জেড ৭৯০ প্লাস ডি৪ (ডিডিআর ৫) ৩৯ হাজার টাকা এবং এইচ ৫১০ এম (ডিডিআর ৪) ১০ হাজার ৫০০ টাকা।\r\n\r\nগিগাবাইট: বি৫৬০ এম (ডিডিআর ৪) ১১ হাজার ৬০০ টাকা, জেড ৬৯০ গেমিং (ডিডিআর ৪) ২৫ হাজার ৫০০ টাকা এবং জেড ৭৯০ ইউ-ডি (ডিডিআর ৫) ৩৩ হাজার টাকা।\r\n\r\nএমএসআই: ম্যাগ বি৭৬০ টোমাহক (ডিডিআর ৪) ২৭ হাজার ৯০০ টাকা, এমপিজি জেড ৬৯০ (ডিডিআর ৪) ৪০ হাজার ৫০০ টাকা এবং প্রো এইচ ৬১০ এম-জি (ডিডিআর ৪) ১১ হাজার ৬০০ টাকা।\r\n\r\nর‌্যাম \r\nজেইল প্রিস্টিন: ৪ গিগাবাইট  (ডিডিআর ৩) ১ হাজার ৩৫০ টাকা।\r\n\r\nট্রানসেন্ড: জেড ৪ জিবি (ডিডিআর ৪) ১ হাজার ৪৫০ টাকা।\r\n\r\nএডেটা: প্রিমিয়ার ৮ জিবি (ডিডিআর ৪) ২ হাজার ১০০ টাকা।\r\n\r\nঅ্যানটেক: ১৬ জিবি (ডিডিআর ৪) ৩২০০ মেগাহার্টজ ৪ হাজার ৫০ টাকা।\r\n\r\nজিস্কিল: এজিস ৩২০০ মেগাহার্টজ ৮ জিবি (ডিডিআর ৪) ২ হাজার ৬০০ টাকা, ট্রাইডেন্ট জেড ৩২০০ মেগাহার্টজ ৮ জিবি (ডিডিআর ৪) ৩ হাজার ৯০০ টাকা।\r\n\r\nগিগাবাইট: অরাস ১৬ জিবি (ডিডিআর ৫) ৫২০০ মেগাহার্টজ ১৪ হাজার টাকা।\r\n\r\nহার্ডডিস্ক ড্রাইভ (এইচডিডি)\r\nসিগেট: বারাকুডা ১ টেরাবাইট (টে. বা.) ৪ হাজার ২০০ টাকা; তোশিবা: ২ টে. বা. ৫ হাজার ৯০০ টাকা, ৪ টে. বা. ১৩ হাজার ৩০০ টাকা, ওয়েস্টার্ন ডিজিটাল ২ টে. বা. ৫ হাজার ৯০০ টাকা।\r\n\r\nসলিড স্টেট ড্রাইভ (এসএসডি)\r\nস্যামসাং: ৯৮০ প্রো ২৫৬ গিগাবাইট (জিবি) ৩ হাজার ৬৭০ টাকা, ৫০০ জিবি ৭ হাজার ২৭০ টাকা এবং ১ টেরাবাইট ১০ হাজার ৬০০ টাকা।\r\n\r\nএইচপি: ইএক্স ৯০০ ১২০ জিবি ১ হাজার ৫৯৯ টাকা, ২৫০ জিবি ২ হাজার টাকা, ৫০০ জিবি ৩ হাজার ১৯৯ টাকা এবং ১ টেরাবাইট ৫ হাজার ৩০০ টাকা।\r\n\r\nমনিটর\r\nএইচপি: ১৮.৫ ইঞ্চি ১০ হাজার ৩০০ টাকা, ১৯.৫ ইঞ্চি ১১ হাজার ৭০০ টাকা, ২৩.৮ ইঞ্চি ২১ হাজার ২০০ টাকা, ৩২ ইঞ্চি ৩৫ হাজার ৭০০ টাকা।\r\n\r\nডেল: এস ই ২২২২ এইচ ২২ ইঞ্চি ১৩ হাজার ৫০০ টাকা, পি ২২২২ এইচ ২৫ হাজার ৫০০ টাকা।\r\n\r\nএমএসআই: প্রো এমপি ২৪১ এক্স ২৩.৮ ইঞ্চি ১৫ হাজার ২০০ টাকা, মডার্ন এমডি ২৪১পি ২৩.৮ ইঞ্চি ২৭ হাজার ৭০০ টাকা।\r\n\r\nবেনকিউ: জি-ডব্লিউ ২২৮০ ২২ ইঞ্চি ১২ হাজার ৫০০ টাকা এবং ই-ডব্লিউ ২৪৮০ ২৩.৮ ইঞ্চি ২৩ হাজার ৫০০ টাকা।\r\n\r\nগিগাবাইট: জি-২৪ এফ ২৩ ৮ ইঞ্চি ২৪ হাজার টাকা, জি২৭ কিউ ২৭ ইঞ্চি ৩৯ হাজার ৯০০ টাকা।\r\n\r\nএলজি: ২৭এমপি৪০০-বি ২৭ ইঞ্চি ২৫ হাজার ৭০০ টাকা, ৩২এমএল৬০০এম-বি ৩২ ইঞ্চি ৩২ হাজার টাকা, ২৪কিউপি৫৫০-বি ২৪ ইঞ্চি ৩৩ হাজার টাকা।\r\n\r\nস্যামসাং: এম-৮ ৩২ ইঞ্চি ৯৬ হাজার টাকা, টি-৫৫ ২৭ ইঞ্চি ২৯ হাজার টাকা, টি-৩৫ ২২ ইঞ্চি ১২ হাজার ৭০০ টাকা।', 'uploads/article/intel.webp', '2023-09-09 01:03:53', 'pc', 0, 4),
(15, 'ইসলামী ব্যাংকে কম্পিউটার সরবরাহ করবে লেনোভো', 'লেনোভোর কম্পিউটার হস্তান্তর অনুষ্ঠানে অতিথিরা', 'ইসলামী ব্যাংককে ১ হাজার ৫০০টি ‘থিঙ্ক সেন্টার নিও ৫০এস’ মডেলের কম্পিউটার সরবরাহ করবে চীনের কম্পিউটার নির্মাতা প্রতিষ্ঠান লেনোভো। গত রোববার রাজধানীর একটি পাঁচ তারকা হোটেলে আয়োজিত কম্পিউটার হস্তান্তর অনুষ্ঠানে এ তথ্য জানানো হয়েছে। \r\n\r\nঅনুষ্ঠানে লেনোভোর ওভারসিস বিজনেস, কনজুমার, কমার্শিয়াল ও ট্যাবলেট বিভাগের মহাব্যবস্থাপক জেনারেল ম্যানেজার নবীন কেজরিওয়াল বলেন, বাংলাদেশে লেনোভোর স্টুডেন্ট ও গেমিং ল্যাপটপ বেশি বিক্রি হলেও বাণিজ্যিক প্রতিষ্ঠানে লেনোভোর প্রযুক্তি পণ্যের বিক্রি বাড়ছে। এটা সরকারের ডিজিটাল ও স্মার্ট বাংলাদেশ উদ্যোগের সুফল। মেয়াদোত্তীর্ণ ল্যাপটপ বা কম্পিউটারের ই-বর্জ্য ব্যবস্থাপনায় বাই–ব্যাক ও গেট–ব্যাক নীতিমালা রয়েছে লেনোভোর।\r\n\r\nবাংলাদেশে লেনোভো পণ্যের বাজারজাতকারী প্রতিষ্ঠান এক্সেল টেকনোলজিসের ব্যবস্থাপনা পরিচালক গৌতম সাহা বলেন, ‘দেশের তরুণ প্রজন্ম স্মার্ট বাংলাদেশ গড়ার মূল শক্তি হিসেবে কাজ করবে। আর তাই আমাদেরকে প্রযুক্তি খাতে বিনিয়োগ করতে হবে।’\r\n\r\nঅনুষ্ঠানে উপস্থিত ছিলেন ইসলামী ব্যাংকের অতিরিক্ত ব্যবস্থাপনা পরিচালক জে কিউ এম হাবিবুল্লাহ, এক্সেল টেকনোলজিসের পরিচালক মাসুদ হোসেন, লেনোভো বাংলাদেশের আঞ্চলিক চ্যানেল ব্যবস্থাপক হাসান রিয়াজ, ব্র্যাক নেটের অপারেশনস বিভাগের প্রধান মোকাররম হোসাইন, কম্পিউটার ভিলেজের ম্যানেজিং পার্টনার মোহাম্মাদ জসিম উদ্দিন প্রমুখ।', 'uploads/article/ibbl.webp', '2023-09-09 01:05:19', 'pc', 25, 4),
(16, 'ভিডিও গেম সংগ্রহ করে গিনেস রেকর্ড', 'বিশ্ব রেকর্ডের সনদ হাতে আন্তোনিও রোমেরো মন্টেরো', 'বিশ্বের সবচেয়ে বেশি ভিডিও গেম সংগ্রহ করে পাঁচবার গিনেস ওয়ার্ল্ড রেকর্ডসে নিজের নাম লিখিয়েছেন যুক্তরাষ্ট্রের টেক্সাসের বাসিন্দা আন্তোনিও রোমেরো মন্টেরো। ৪৫ বছর বয়সী রোমেরোর সংগ্রহে ২৪ হাজার ২৬৮টি ভিডিও গেম রয়েছে। এসব গেমের বর্তমান বাজারদর প্রায় ২১ লাখ ডলার।\r\n\r\nমেইল অনলাইনের এক প্রতিবেদনে বলা হয়েছে, আন্তোনিও রোমেরো মন্টেরো মোট পাঁচবার গিনেস ওয়ার্ল্ড রেকর্ড করেছেন। সবচেয়ে বেশি এক্স বক্স গেম, সবচেয়ে বেশি সেগা গেম, সবচেয়ে বেশি নিন্টেন্ডো গেম, সবচেয়ে বেশি প্লে স্টেশন গেম এবং সবচেয়ে বেশি ভিডিও গেম সংগ্রহকারী হিসেবে রেকর্ডগুলো করেন তিনি।\r\n\r\nআন্তোনিও রোমেরো মন্টেরো জানিয়েছেন, ১০ বছর বয়স থেকেই ভিডিও গেম সংগ্রহ করেন তিনি। সংগ্রহের তালিকায় এক্স বক্স, সেগা, নিন্টেন্ডো, প্লে স্টেশনের পাশাপাশি বিভিন্ন প্রতিষ্ঠানের ভিডিও গেম, গেমিং কনসোল এবং যন্ত্রাংশও রয়েছে। কোনো পরিকল্পনা করে নয়, শখের বশেই এগুলো সংগ্রহ করা হয়েছে।\r\n\r\nকিশোর বয়স থেকে সেগা গেম কেনার পর ধীরে ধীরে সংগ্রহের পরিমাণ বাড়তে থাকে আন্তোনিওর। নিজ ঘরের পুরো একটি রুমজুড়ে ভিডিও গেমগুলো সাজিয়ে রেখেছেন তিনি। প্রায় ২৫ হাজার গেম সংগ্রহে থাকলেও আন্তোনিওর প্রিয় গেম সুপার নিন্টেন্ডো ও সুপার ক্যাসলেভানিয়া।', 'uploads/article/game.webp', '2023-09-09 01:20:03', 'gaming', 10, 4),
(17, 'সাড়ে ছয় লাখ কোটি টাকায় ভিডিও গেম কোম্পানি কেনার পরিকল্পনা মাইক্রোসফটের', 'অ্যাকটিভিশন ব্লিজার্ডকে অধিগ্রহণের পরিকল্পনা করছে', 'যুক্তরাষ্ট্রের বিশ্বখ্যাত প্রযুক্তি প্রতিষ্ঠান মাইক্রোসফট ৬ হাজার ৮৭০ কোটি মার্কিন ডলারে সেই দেশের বহুজাতিক ভিডিও গেম নির্মাতা অ্যাকটিভিশন ব্লিজার্ডকে অধিগ্রহণের পরিকল্পনা করছে। বাংলাদেশি মুদ্রায় অ্যাকটিভিশন ব্লিজার্ডের দাম পড়ে প্রায় ৫ লাখ ৯১ হাজার কোটি টাকা। বিবিসিসহ বিভিন্ন আন্তর্জাতিক গণমাধ্যম এ খবর পরিবেশন করেছে।\r\n\r\nতবে যুক্তরাষ্ট্রের প্রভাবশালী দৈনিক পত্রিকা ওয়াল স্ট্রিট জার্নাল বলেছে, মাইক্রোসফট ৭ হাজার ৫০০ কোটি ডলারে অ্যাকটিভিশন ব্লিজার্ডকে কিনে নিচ্ছে। সে ক্ষেত্রে কোম্পানিটির দাম দাঁড়ায় ৬ লাখ ৪৫ হাজার কোটি টাকা (প্রতি ডলার ৮৬ টাকা ধরে)। দুটো দামই বাংলাদেশের এক বছরের বাজেটের চেয়ে বেশি। বাংলাদেশের চলতি ২০২১–২২ অর্থবছরের জাতীয় বাজেটের আকার হলো ৬ লাখ ৩ হাজার ৬৮১ কোটি টাকা।\r\n\r\nঅ্যাকটিভিশন ব্লিজার্ডকে কিনলে এটি হবে মাইক্রোসফটের এযাবৎকালের সবচেয়ে বড় অধিগ্রহণ চুক্তি। আগামী ২০২৩ সালে এ অধিগ্রহণ প্রক্রিয়া চূড়ান্ত হবে। এর ফলে মাইক্রোসফট একাধারে শুটার ভিডিও গেম নির্মাতা কল অব ডিউটি, বিনোদন ভিডিও প্রস্তুতকারক ওয়ারক্রাফট ও মাল্টিপ্লেয়ার শুটার গেম নির্মাতা ওভারওয়াচের মতো গেমিং ফ্র্যাঞ্চাইজি তথা প্রযুক্তি প্রতিষ্ঠানগুলোর মালিক হয়ে যাবে।\r\n\r\nএ অধিগ্রহণ চুক্তিটি হবে গেমিংয়ের ইতিহাসে সবচেয়ে বড়। মাইক্রোসফট আরেক জনপ্রিয় গেমিং কোম্পানি বেথেসডা কেনার এক বছর পরে এসে অ্যাকটিভিশন ব্লিজার্ডকে অধিগ্রহণের ঘোষণা দিল।\r\n\r\nমাইক্রোসফটের চেয়ারম্যান ও প্রধান নির্বাহী কর্মকর্তা (সিইও) সত্য নাদেলা এ প্রসঙ্গে বলেন, ‘আমরা গেমিংয়ের জগতে একটি নতুন যুগের সূচনা করতে বিশ্বমানের গেম প্রস্তুতকারক কোম্পানিতে বিনিয়োগ করতে যাচ্ছি। এর ফলে খেলোয়াড় ও নির্মাতারা নিরাপদ গেম দেখা ও তৈরির সুযোগ পাবেন।’\r\n\r\nঅ্যাকটিভিশন ব্লিজার্ডের তথ্য অনুযায়ী, বিশ্বব্যাপী তাদের অনেকগুলো গেম স্টুডিও রয়েছে। সব মিলিয়ে তাদের মোট জনবল হচ্ছে ১০ হাজার, যাদের সবার চাকরি এ অধিগ্রহণ প্রক্রিয়া সম্পন্ন হওয়ার পর নতুন কোম্পানিতে অর্থাৎ মাইক্রোসফটে ন্যস্ত হবে।\r\n\r\nঅ্যাকটিভিশন ব্লিজার্ড হচ্ছে যুক্তরাষ্ট্রের ক্যালিফোর্নিয়া রাজ্যের উপকূলীয় শহর সান্টা মনিকাভিত্তিক একটি তথ্যপ্রযুক্তি প্রতিষ্ঠান। এটি মূলত ভিডিও গেম তৈরি করে থাকে। ২০০৮ সালের জুলাইয়ে এটির যাত্রা শুরু হয়। \r\n\r\nসাম্প্রতিক মাসগুলোতে অবশ্য যৌন হয়রানি, অসদাচরণসহ বিভিন্ন কারণে অ্যাকটিভিশন ব্লিজার্ডে কর্মপরিবেশ যেন বিষাক্ত হয়ে ওঠে। এ রকম পরিবেশে আর কাজ না করে কোম্পানিটি ছেড়ে চলে যাচ্ছেন এর প্রধান নির্বাহী কর্মকর্তা (সিইও) ববি কোটিক।\r\n\r\nবিভিন্ন সময় মাইক্রোসফট আরও কয়েকটি কোম্পানি অধিগ্রহণ করে। এসব কোম্পানির মধ্যে রয়েছে ২০১৬ সালে ২ হাজার ৬২০ কোটি ডলারে লিংকডইন, ২০২১ সালে ১ হাজার ৬০০ কোটি ডলারে নুয়ান্স, ২০১১ সালে ৮৫০ কোটি ডলারে স্কাইপে, ২০২০ সালে ৮১০ কোটি ডলারে জেনিম্যাক্স মিডিয়া, ২০১৮ সালে ৭৫০ কোটি ডলারে গিটহাব, ২০০৭ সালে ৫৯০ কোটি ডলারে অ্যাকুয়ান্টিভ, ২০১৫ সালে ৫৩০ কোটি ডলারে ইনফরমেটিকা ও ২০১৪ সালে ২৫০ কোটি ডলারে মোজাং।\r\n\r\nঅ্যাকটিভিশন ব্লিজার্ড কল অব ডিউটি ও ক্যান্ডি ক্র্যাশের মতো বিশ্বব্যাপী জনপ্রিয় হয়ে ওঠা কিছু গেম তৈরি করে। বর্তমানে তাদের তৈরি গেমগুলো বিশ্বের ১৯০টি দেশে খেলা হচ্ছে। এসব গেমের গড় মাসিক খেলোয়াড় সংখ্যা প্রায় ৪০ কোটি।\r\n\r\nঅন্যদিকে মাইক্রোসফটও হচ্ছে যুক্তরাষ্ট্রের বহুজাতিক প্রযুক্তি কোম্পানি। বিশ্বের শীর্ষস্থানীয় অতিধনী বিল গেটস ১৯৭৫ সালের ৪ এপ্রিল পল অ্যালেনকে সঙ্গে নিয়ে এ কোম্পানি প্রতিষ্ঠা করেন। এটির পণ্য ও সেবাগুলোর মধ্যে রয়েছে কম্পিউটার সফটওয়্যার, কনজ্যুমার ইলেকট্রনিকস, পিসি (পারসোনাল কম্পিউটার), ভিডিও গেম ইত্যাদি। বিল গেটস এখন ১৩ হাজার ৩৩০ কোটি ডলারের সম্পদ নিয়ে বিশ্বের চতুর্থ শীর্ষ ধনী। এদিকে মাইক্রোসফট বাজার মূলধনে বর্তমানে বিশ্বে দ্বিতীয় স্থানে রয়েছে। এটির বর্তমান বাজারমূল্য আড়াই লাখ কোটি ডলার। শীর্ষস্থানে রয়েছে যুক্তরাষ্ট্রেরই আরেক বহুজাতিক প্রযুক্তি কোম্পানি অ্যাপল। সেটির বাজার মূলধন সম্প্রতি তিন লাখ কোটি ডলার ছাড়িয়ে যায়।', 'uploads/article/ms.webp', '2023-09-09 01:22:01', 'gaming', 2, 4),
(18, 'শিশু-কিশোরদের জন্য স্ক্র্যাচ প্রোগ্রামিং কর্মশালা', '৮ থেকে ১৫ বছরের শিক্ষার্থীদের প্রোগ্রামিং কর্মশালা', 'প্রাথমিক পর্যায়ের শিক্ষার্থীদের স্ক্র্যাচ প্রোগ্রামিং শেখাতে যৌথভাবে প্রোগ্রামিং কর্মশালার আয়োজন করেছে বাংলাদেশ ওপেন সোর্স নেটওয়ার্ক (বিডিওএসএন) এবং স্ক্র্যাচ বাংলাদেশ। ২৮ আগস্ট শুরু হতে যাওয়া ৬ দিনের এ কর্মশালায় ৮ থেকে ১৫ বছর বয়সী শিক্ষার্থীরা অংশ নিতে পারবে। আজ এক সংবাদ বিজ্ঞপ্তিতে এ তথ্য জানিয়েছে বিডিওএসএন।\r\n\r\nসংবাদ বিজ্ঞপ্তিতে বলা হয়, যুক্তরাষ্ট্রের ম্যাসাচুসেটস ইনস্টিটিউট অব টেকনোলজি (এমআইটি) উদ্ভাবিত স্ক্র্যাচ প্রোগ্রাম মূলত ছবি ও নকশাভিত্তিক প্রোগ্রামিং ভাষা, যা দিয়ে খুব সহজেই প্রোগ্রাম করার পদ্ধতি শেখা যায়। এর ফলে শিশুরা খেলার ছলে স্ক্র্যাচ প্রোগ্রামিংয়ের মাধ্যমে গেম, অ্যানিমেশন ভিডিও, কার্টুন বা ক্যালকুলেটরে ব্যবহৃত সফটওয়্যারসহ বিভিন্ন সফটওয়্যার তৈরি করতে পারে।\r\n\r\nস্ক্র্যাচ প্রোগ্রামিং কর্মশালা পরিচালনা করবেন ‘ছোটদের স্ক্র্যাচ প্রোগ্রামিং’ বইয়ের লেখক মোশারফ হোসেন। এরই মধ্যে কর্মশালার নিবন্ধন কার্যক্রম শুরু হয়েছে। শিক্ষার্থীরা https://forms.gle/TygWkWJdgXA4Jccu8  ঠিকানায় ক্লিক করে নিবন্ধন করার পাশাপাশি কর্মশালা সম্পর্কে বিস্তারিত তথ্য জানতে পারবে।', 'uploads/article/pro.webp', '2023-09-09 01:25:12', 'programing', 0, 4),
(19, 'স্ট্যানফোর্ড বিশ্ববিদ্যালয়ে বিনা মূল্যে পাইথন প্রোগ্রামিং শেখার সুযোগ', 'পাইথন প্রোগ্রামিংয়ের কোর্স করা যাবে বিনা মূল্যে', 'যুক্তরাষ্ট্রের স্ট্যানফোর্ড বিশ্ববিদ্যালয়ে পাইথন প্রোগ্রামিংয়ের কোর্স করা যাবে বিনা মূল্যে। কোর্সটি অবশ্যই ছয় সপ্তাহের মধ্যে শেষ করতে হবে। প্রতি সপ্তাহে একাধিক ভিডিও ক্লাস ছাড়াও একজন মেন্টরের তত্ত্বাবধানে অনলাইনে রিভিউ করার সুযোগ থাকছে। অনেক সময় স্ট্যানফোর্ডের শিক্ষার্থীরাও এ কোর্সে মেন্টর হিসেবে কাজ করবেন।\r\n\r\n‘কোড ইন প্লেস’ নামের এই কোর্সে বিশ্বের যেকোনো প্রান্তের শিক্ষার্থীরা অনলাইনে অংশ নিতে পারবেন। স্ট্যানফোর্ড বিশ্ববিদ্যালয় এবং একই বিশ্ববিদ্যালয়ের পিচ ল্যাবের যৌথ প্রচেষ্টায় আয়োজিত হচ্ছে এই ‘কোড ইন প্লেস’। স্ট্যানফোর্ডের কম্পিউটারবিজ্ঞান বিভাগের দুজন অধ্যাপক ক্রিস পিচ ও মেহরান সাহামি এ প্রকল্পের উদ্যোক্তা। বাংলাদেশ থেকেও এই কোর্সে শিক্ষার্থী কিংবা মেন্টর হিসেবে অংশগ্রহণের জন্য আবেদন করা যাবে।\r\n\r\nছয় সপ্তাহের এ কোর্সে প্রতি সপ্তাহে একটি বিষয়ে একাধিক ভিডিও ক্লাস করতে হবে শিক্ষার্থীদের। ছয় সপ্তাহের বিষয়বস্তুর মধ্যে আছে কন্ট্রোল ফ্লো উইথ কারেল, দ্য আর্ট অব কোডিং, কনসোল প্রোগ্রামিং, আন্ডারস্ট্যান্ডিং ভেরিয়েবল, গ্রাফিকস, লিস্ট ও ডিকশনারি। নিজের পছন্দমতো সময়ে ভিডিও ক্লাসগুলো দেখার সুযোগ পাবেন শিক্ষার্থীরা। সম্পূর্ণ কোর্সটি পাইথন প্রোগ্রামিং ভাষার মাধ্যমে সম্পন্ন করা হবে।\r\nhttps://codeinplace.stanford.edu ঠিকানার ওয়েবসাইটে গিয়ে নির্ধারিত ফরমে আবেদন করতে হবে আগ্রহীদের। আবেদনের শেষ দিন ১০ এপ্রিল।', 'uploads/article/stan.webp', '2023-09-09 01:26:37', 'programing', 1, 4),
(20, 'দেশে ইন্টারনেট ব্যবহারকারীর সংখ্যা আসলে কত', 'ইন্টারনেট ব্যবহারকারীর সংখ্যা ১৩ কোটি ছাড়িয়েছে', 'সরকারের বিভিন্ন সংস্থা বিভিন্ন পদ্ধতিতে হিসাব করে ইন্টারনেট ব্যবহারকারীর বিভিন্ন রকম তথ্য দিচ্ছে। ফলে কোন তথ্যটি ব্যবহারযোগ্য, তা নিয়ে বিভ্রান্তি তৈরি হয়েছে। \r\n\r\nবাংলাদেশ টেলিযোগাযোগ নিয়ন্ত্রণ কমিশনের (বিটিআরসি) হিসাবে, গত জুলাই মাস শেষে দেশে ইন্টারনেট ব্যবহারকারীর সংখ্যা ১৩ কোটি ছাড়িয়েছে। \r\n\r\nবাংলাদেশ পরিসংখ্যান ব্যুরোর (বিবিএস) জনশুমারি ও গৃহগণনা-২০২২–এর প্রাথমিক প্রতিবেদন বলছে, ইন্টারনেট ব্যবহারকারী জনসংখ্যার ৩১ শতাংশ। সংস্থাটির তথ্য ও যোগাযোগপ্রযুক্তি (আইসিটি) জরিপের তথ্য অনুযায়ী, ইন্টারনেট ব্যবহারকারী ৩৯ শতাংশ। \r\n\r\nসংশ্লিষ্ট ব্যক্তিরা বলছেন, একেক রকম পরিসংখ্যান না দিয়ে প্রকৃত ইন্টারনেট ব্যবহারকারীর সংখ্যাটি নিয়মিত প্রকাশ করা জরুরি।ঢাকা বিশ্ববিদ্যালয়ের তথ্যপ্রযুক্তি ইনস্টিটিউটের পরিচালক অধ্যাপক বি এম মইনুল হোসেন বলেন, ইন্টারনেট ব্যবহারকারীর সংখ্যার ক্ষেত্রে বিবিএসের পরিসংখ্যানকেই ভিত্তি হিসেবে ধরা যায়। কারণ, বিটিআরসি তিন মাসে যে একবার ইন্টারনেট ব্যবহার করেছেন, তাঁকেই গ্রাহক ধরে। বিটিআরসির উচিত সর্বশেষ এক সপ্তাহ বা এক মাসে কেউ ইন্টারনেট ব্যবহার করলে তাঁকে গ্রাহক হিসেবে ধরা।\r\n\r\nসর্বশেষ জনশুমারি অনুযায়ী দেশের জনসংখ্যা ১৬ কোটি ৯৮ লাখ। বিটিআরসির হিসাবে, জুলাই শেষে দেশে মুঠোফোন ব্যবহারকারীর সংখ্যা দাঁড়িয়েছে প্রায় ১৮ কোটি ৭৫ লাখে। এ ক্ষেত্রেও সর্বশেষ ৯০ দিনে একবার ব্যবহার করলেই তাঁকে ব্যবহারকারী ধরা হয়। আর একজনের কাছে একাধিক সিম থাকতে পারে।\r\n\r\nইন্টারনেট ব্যবহারকারীর ক্ষেত্রেও সমস্যাটি একই। বিটিআরসির হিসাবে, জুলাই শেষে দেশে ইন্টারনেট ব্যবহারকারীর সংখ্যা দাঁড়িয়েছে ১৩ কোটি ১ লাখে। যদিও এ হিসাবের ক্ষেত্রে সর্বশেষ ৯০ দিনে একবার ব্যবহার করলেই কোনো ব্যক্তি ইন্টারনেট ব্যবহারকারী হিসেবে গণ্য হন। এক ব্যক্তি একাধিক সিম বা মুঠোফোন ব্যবহার করলে তাঁকে একাধিক ব্যবহারকারী হিসেবে গণ্য করা হয়। ফলে জানা যায় না, দেশে আসলে কত মানুষ ইন্টারনেট ব্যবহার করেন। ', 'uploads/article/btrc.webp', '2023-09-09 06:57:25', 'smartphone', 2, 4);
